import "./App.css";
import { useState } from "react";

function App() {
  const [activity, setActivity] = useState("");
  const [list, setlist] = useState([]);

  function addActivity() {
    setlist((list) => {
      const updatedList = setlist([...list, activity]);
      setActivity("");
      console.log(setlist);
      return updatedList;
    });
  }

  return (
    <>
      <div>
        <div>
          <input
            type="text"
            placeholder="Enter your Activity"
            value={activity}
            onChange={(e) => setActivity(e.target.value)}
          />
          <button onClick={addActivity}>Add</button>
        </div>
        <p className="list-heading">{setlist}</p>
      </div>
    </>
  );
}

export default App;
